```rust
use clap::{Parser, Subcommand};

pub mod commands;

/// Cargo subcommand wrapper (handles 'cargo upkeep' invocation)
#[derive(Parser)]
#[command(name = "cargo")]
#[command(bin_name = "cargo")]
pub struct Cargo {
    #[command(subcommand)]
    pub command: CargoCommands,
}

#[derive(Subcommand)]
pub enum CargoCommands {
    /// Unified Rust project maintenance CLI
    Upkeep(UpkeepArgs),
}

#[derive(clap::Args)]
pub struct UpkeepArgs {
    #[command(subcommand)]
    pub command: Commands,

    /// Enable verbose output (info level logging)
    #[arg(long, global = true)]
    pub verbose: bool,

    /// Set log level (trace, debug, info, warn, error)
    #[arg(long, global = true)]
    pub log_level: Option<String>,

    /// Output as JSON
    #[arg(long, global = true)]
    pub json: bool,
}

#[derive(Subcommand)]
pub enum Commands {
    /// Detect project configuration
    Detect,
    /// Check for outdated dependencies
    Deps,
    /// Run security audit
    Audit,
    /// Generate quality score
    Quality,
    /// Find unused dependencies
    Unused,
    /// Report unsafe code usage
    Unsafe,
    /// Show dependency tree
    Tree,
}
```

